import React from "react";
import "../App.css";

const HeroSection: React.FC = () => {
  return (
    <section
      style={{
        padding: "2.5rem 0 1rem",
        background: "linear-gradient(135deg, #f7f3ff, #f0f9ff)",
      }}
    >
      <div className="app-container">
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "minmax(0, 2fr) minmax(0, 3fr)",
            gap: "2rem",
            alignItems: "center",
          }}
        >
          {/* Left side: text */}
          <div>
            <h1
              style={{
                fontSize: "2.4rem",
                margin: 0,
                marginBottom: "0.75rem",
              }}
            >
              Learn anything, anytime, from anywhere.
            </h1>
            <p
              style={{
                fontSize: "1rem",
                color: "var(--text-muted)",
                marginBottom: "1.25rem",
              }}
            >
              Build skills with high-quality courses taught by real-world
              experts.
            </p>

            <div
              style={{
                backgroundColor: "#fff",
                padding: "0.75rem",
                borderRadius: "999px",
                boxShadow: "0 10px 30px rgba(15, 23, 42, 0.08)",
                display: "flex",
                gap: "0.5rem",
                alignItems: "center",
                maxWidth: "480px",
              }}
            >
              <input
                placeholder="What do you want to learn?"
                style={{
                  flex: 1,
                  border: "none",
                  outline: "none",
                  fontSize: "0.95rem",
                }}
              />
              <button
                style={{
                  padding: "0.55rem 1.2rem",
                  borderRadius: "999px",
                  border: "none",
                  background: "var(--primary)",
                  color: "#fff",
                  cursor: "pointer",
                  fontWeight: 600,
                  fontSize: "0.9rem",
                }}
              >
                Search
              </button>
            </div>

            <p
              style={{
                marginTop: "0.75rem",
                fontSize: "0.85rem",
                color: "var(--text-muted)",
              }}
            >
              Join over <strong>50 million</strong> learners worldwide.
            </p>
          </div>

          {/* Right side: simple info card */}
          <div
            style={{
              backgroundColor: "#fff",
              borderRadius: "12px",
              padding: "1.5rem",
              boxShadow: "0 10px 30px rgba(15, 23, 42, 0.08)",
            }}
          >
            <p
              style={{
                fontWeight: 600,
                marginBottom: "0.5rem",
                fontSize: "0.95rem",
              }}
            >
              A broad selection of courses
            </p>
            <p
              style={{
                fontSize: "0.9rem",
                color: "var(--text-muted)",
              }}
            >
              Choose from thousands of online video courses with new additions
              published every month.
            </p>
            <ul
              style={{
                marginTop: "1rem",
                paddingLeft: "1.25rem",
                fontSize: "0.9rem",
                color: "var(--text-muted)",
              }}
            >
              <li>Hands-on projects and downloadable resources</li>
              <li>Lifetime access on mobile and desktop</li>
              <li>Certificates of completion</li>
            </ul>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
