import React from "react";
import "../App.css";

const Header: React.FC = () => {
  return (
    <header
      style={{
        position: "sticky",
        top: 0,
        zIndex: 20,
        backgroundColor: "#ffffff",
        borderBottom: "1px solid var(--border-subtle)",
        boxShadow: "0 2px 4px rgba(15, 23, 42, 0.04)",
      }}
    >
      <div
        className="app-container"
        style={{ paddingTop: "0.75rem", paddingBottom: "0.75rem" }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            gap: "1rem",
          }}
        >
          {/* Logo */}
          <div
            style={{
              fontWeight: 800,
              fontSize: "1.25rem",
              color: "var(--primary)",
            }}
          >
            Udemy<span style={{ color: "#000" }}>Clone</span>
          </div>

          {/* Navigation + Search */}
          <nav
            style={{
              display: "flex",
              alignItems: "center",
              gap: "1rem",
              fontSize: "0.9rem",
              flex: 1,
            }}
          >
            <span>Categories</span>

            {/* Search bar */}
            <input
              placeholder="Search for anything"
              style={{
                flex: 1,
                padding: "0.55rem 0.75rem",
                borderRadius: "999px",
                border: "1px solid var(--border-subtle)",
                outline: "none",
                fontSize: "0.9rem",
              }}
            />

            <span style={{ color: "var(--text-muted)" }}>Udemy Business</span>
            <span style={{ color: "var(--text-muted)" }}>Teach on Udemy</span>
          </nav>

          {/* Login / Signup Buttons */}
          <div style={{ display: "flex", gap: "0.5rem" }}>
            <button
              style={{
                padding: "0.5rem 0.9rem",
                borderRadius: "0",
                border: "1px solid var(--text-main)",
                background: "#fff",
                cursor: "pointer",
                fontSize: "0.9rem",
              }}
            >
              Log in
            </button>

            <button
              style={{
                padding: "0.5rem 0.9rem",
                borderRadius: "0",
                border: "1px solid var(--text-main)",
                background: "var(--primary)",
                color: "#fff",
                cursor: "pointer",
                fontSize: "0.9rem",
              }}
            >
              Sign up
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
